import React, { Component } from "react";
import { GridContext } from "../contexts/GridContext";

import Note from "./Note";

import "../App";

class GridLine extends Component {
  static contextType = GridContext;

  render() {
    return (
      <div className="grid-line">
        <div
          className="instr-name"
          id={this.props.instrument}
          onClick={() => {
            this.context.playNote(this.props.instrument);
            this.context.handleIt(this.props.instrument);
          }}
        >
          {this.context.instrumentLabels[this.props.instrument]}
        </div>
        <div className="notes">
          {instruments[props.instrument].map((note, index) => (
            <Note
              class={`note ${
                note && index === currentDivision
                  ? "note-playing"
                  : note
                  ? "note-active"
                  : ""
              }`}
              note={index}
              instrument={props.instrument}
              setGrid={setGrid}
              key={index}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default GridLine;
