import React from "react";

import { GridContext } from "../contexts/GridContext";
import "../App";

export default class BPMContainer extends React.Component {
  static contextType = GridContext;

  increaseBpm = (interval, increment) => {
    this.context.setBpm(this.context.bpm + increment);
    let start = true;
    window.addEventListener("mouseup", () => {
      console.log("mouseup");
      start = false;
    });
    console.log(start);
    if (interval > 100) interval -= 10;
    if (start) {
      console.log("startis: ", start);
      setTimeout(() => {
        this.increaseBpm(interval, increment);
      }, interval);
    }
  };

  render() {
    return (
      <div className="bpm-container">
        <p id="bpm-desc">BPM</p>
        <div id="bpm-text">
          <p>{this.context.bpm}</p>
        </div>
        <div id="arrows">
          <p className="arrow" onMouseDown={() => this.increaseBpm(200, 1)}>
            ▲
          </p>
          <p
            className="arrow"
            onClick={() => this.context.setBpm(this.context.bpm - 2)}
          >
            ▼
          </p>
        </div>
      </div>
    );
  }
}
