import React from "react";

import { GridContext } from "../contexts/GridContext";
import "../App";

export default class BPMContainer extends React.Component {
  static contextType = GridContext;

  render() {
    return (
      <div className="bpm-container">
        <p id="bpm-desc">BPM</p>
        <div id="bpm-text">
          <p>{props.bpm}</p>
        </div>
        <div id="arrows">
          <p className="arrow" onMouseDown={() => props.setBpm(props.bpm + 2)}>
            ▲
          </p>
          <p className="arrow" onClick={() => props.setBpm(props.bpm - 2)}>
            ▼
          </p>
        </div>
      </div>
    );
  }
}
