import React, { useContext } from "react";
import { GridContext } from "../contexts/GridContext";

import Note from "./Note";

import "../App";

function GridLine(props) {
  const { instrumentLabels, setGrid, currentDivision } = useContext(
    GridContext
  );
  return (
    <div className="grid-line">
      <div className="instr-name">{instrumentLabels[props.instrument]}</div>
      <div className="notes">
        {instruments[props.instrument].map((note, index) => (
          <Note
            class={`note ${
              note && index === currentDivision
                ? "note-playing"
                : note
                ? "note-active"
                : ""
            }`}
            note={index}
            instrument={props.instrument}
            setGrid={setGrid}
            key={index}
          />
        ))}
      </div>
    </div>
  );
}

export default GridLine;
